let slideIndex = 0;

function showSlides() {
    const slides = document.getElementsByClassName("mySlides");
    for (let i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";  
    }
    slideIndex++;
    if (slideIndex > slides.length) { slideIndex = 1 }    
    slides[slideIndex - 1].style.display = "block";  
    setTimeout(showSlides, 3000); // Change image every 3 seconds
}

function plusSlides(n) {
    slideIndex += n;
    const slides = document.getElementsByClassName("mySlides");
    if (slideIndex > slides.length) { slideIndex = 1 }
    if (slideIndex < 1) { slideIndex = slides.length }
    showSlidesManual();
}

function showSlidesManual() {
    const slides = document.getElementsByClassName("mySlides");
    for (let i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";  
    }
    slides[slideIndex - 1].style.display = "block";  
}

// Call showSlides initially to display the first image
showSlides();

// JavaScript to handle brand selection and form submission
const brandList = document.getElementById('brandList');
const brandError = document.getElementById('brandError');
let selectedBrand = '';

// Add click event listener for brand selection
brandList.addEventListener('click', (event) => {
  const brandDiv = event.target.closest('.brand');
  if (brandDiv) {
    // Remove selected class from all brands
    document.querySelectorAll('.brand').forEach(brand => brand.classList.remove('selected'));
    
    // Add selected class to clicked brand
    brandDiv.classList.add('selected');
    
    // Set selected brand
    selectedBrand = brandDiv.dataset.brand;
    brandError.textContent = '';
  }
});

// Form validation and submission
const inquiryForm = document.getElementById('inquiryForm');
inquiryForm.addEventListener('submit', (event) => {
  event.preventDefault();

  // Basic validation
  let valid = true;
  if (selectedBrand === '') {
    brandError.textContent = 'Please select a brand';
    valid = false;
  }

  if (!inquiryForm.fullName.value) {
    document.getElementById('fullNameError').textContent = 'Full name is required';
    valid = false;
  } else {
    document.getElementById('fullNameError').textContent = '';
  }

  if (!inquiryForm.email.value) {
    document.getElementById('emailError').textContent = 'Email is required';
    valid = false;
  } else {
    document.getElementById('emailError').textContent = '';
  }

  if (!inquiryForm.mobile.value) {
    document.getElementById('mobileError').textContent = 'Mobile is required';
    valid = false;
  } else {
    document.getElementById('mobileError').textContent = '';
  }

  if (!inquiryForm.product.value) {
    document.getElementById('productError').textContent = 'Please select a product';
    valid = false;
  } else {
    document.getElementById('productError').textContent = '';
  }

  if (!inquiryForm.quantity.value) {
    document.getElementById('quantityError').textContent = 'Quantity is required';
    valid = false;
  } else {
    document.getElementById('quantityError').textContent = '';
  }

  if (!inquiryForm.shippingTo.value) {
    document.getElementById('shippingToError').textContent = 'Shipping location is required';
    valid = false;
  } else {
    document.getElementById('shippingToError').textContent = '';
  }

  if (valid) {
    // Process form submission (e.g., send data to the server)
    const formData = new FormData(inquiryForm);
    formData.append('brand', selectedBrand);
    console.log(Object.fromEntries(formData.entries()));
    alert('Form submitted successfully!');
  }
});
